/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { AuthForm, SubmitButton } from './AuthStyle'


const Signup = () => {
  const [invitation, setInvitation] = useState(true);
  const [register, setRegister] = useState({
    name: '',
    email: '',
    password: '',
  });
  const {
    name,
    email,
    password,
  } = register;

  useEffect(() => {
    const errors = {
      firstName: '',
      lastName: '',
      email: '',
    };
    setFormErrors(errors);
  }, [name, email, password]);

  const onInputChange = (evt) => {
    setRegister({
      ...register,
      [evt.target.name]: evt.target.value,
    });
  };

  const onFormSubmit = (evt) => {
    evt.preventDefault();
    console.log("Hello")
  };

  return (
    <AuthForm>
      <form
        onSubmit={onFormSubmit}
        method="post"
      >
        <div className="form-group">
          <label className="input-label" for="email">Name:</label>
          <input 
            type="name" 
            className="form-control" 
            id="name"
            value={name}
            onChange={onInputChange}
          />
        </div>
        <div className="form-group">
          <label className="input-label" for="email">Email address:</label>
          <input 
            type="email" 
            className="form-control" 
            id="email" 
            value={email}
            onChange={onInputChange}
          />
        </div>
        <div className="form-group">
          <label className="input-label" for="pwd">Password:</label>
          <input 
            type="password" 
            className="form-control" 
            id="pwd"
            value={password}
            onChange={onInputChange}
          />
        </div>
        <div className="checkbox">
          <label className="input-label"><input type="checkbox" /> Remember me</label>
        </div>
        <br></br>
        <SubmitButton className="form-group">
          <button type="submit" className="btn btn-default">Submit</button>
        </SubmitButton>
      </form>
    </AuthForm>
  );
};


export default Signup;
