/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';
import { ACCESS_TOKEN } from '../Constant';
import { UnitPrice, CustomSelectOption, CustomInput, NewOrderCell } from './OrderStyle';


const NewOrder = () => {
  const [cookies] = useCookies([ACCESS_TOKEN]);
  const token = cookies[ACCESS_TOKEN];
  const [cardBoards, setCardBoards] = useState([]);
  const [selectedCard, setSelectedCard] = useState('');
  const [quantity, setQuantity] = useState(0);

  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' },
  ];


  const confirm = (response) => {
    const { cardboard_type } = response;
    console.log(cardboard_type);
    setCardBoards(cardboard_type)
  }

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/cardboard_types", {
      "method": "GET",
      "headers": {
        "content-type": "application/json",
        "accept": "application/json",
        "Authorization": "token eyJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoxLCJleHAiOjM2MDAwMDAwMDE1OTM4MDkwOTZ9.w4XiVPFidW2dcCu62m8ZcLXw5RR7-tuNLT4SpdfJr_0"
      },
    })
    .then(response => response.json())
    .then(response => {
      confirm(response);
    })
    .catch(err => {
      debugger
      console.log(err);
    });
  }, []);

  const handleChange = (value) => {
    debugger
  }

  const onInputChange= (evt) => {
    setQuantity(evt.target.value);
  }

  const handleSelectOption = (evt) => {
    debugger
    setSelectedCard(evt.target.value)
  }


  if (!token) {
    window.location.href = '/login';
  }


  return (
    <div>
      <NewOrderCell className="d-flex flex-row">
        <div className="order_column head">CardBoard #</div>
        <div className="order_column head">Card Type</div>
        <div className="order_column head">Price</div>
        <div className="order_column head">Qty</div>
        <div className="order_column head">Total</div>
      </NewOrderCell>
      {cardBoards.map((card, index) => (
        <NewOrderCell className="d-flex flex-row" key={index}>
          <div className="order_column">{index + 1}</div>
          <div className="order_column">{card.card_type}</div>
          <div className="order_column">{card.price}</div>
          <div className="order_column">
            <CustomInput className="order_column head">
              <input 
                type="number" 
                value={quantity} 
                name="quantity"
                onChange={onInputChange}
              />
            </CustomInput>
          </div>
        </NewOrderCell>
      ))}
    </div>
  );
};


export default NewOrder;
