/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';
import { ACCESS_TOKEN } from '../Constant';
import { UnitPrice, CustomSelectOption, CustomInput } from './OrderStyle';


const NewOrder = () => {
  const [cookies] = useCookies([ACCESS_TOKEN]);
  const token = cookies[ACCESS_TOKEN];
  const [cardBoards, setCardBoards] = useState([]);
  const [selectedOption, setSelectedOption] = useState('');
  const [quantity, setQuantity] = useState(0);

  const options = [
    { value: 'chocolate', label: 'Chocolate' },
    { value: 'strawberry', label: 'Strawberry' },
    { value: 'vanilla', label: 'Vanilla' },
  ];


  const confirm = (response) => {
    const { cardboard_type } = response;
    console.log(cardboard_type);
    setCardBoards(cardboard_type)
  }

  useEffect(() => {
    fetch("http://localhost:3000/api/v1/cardboard_types", {
      "method": "GET",
      "headers": {
        "content-type": "application/json",
        "accept": "application/json",
        "Authorization": "token eyJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjoxLCJleHAiOjM2MDAwMDAwMDE1OTM4MDkwOTZ9.w4XiVPFidW2dcCu62m8ZcLXw5RR7-tuNLT4SpdfJr_0"
      },
    })
    .then(response => response.json())
    .then(response => {
      confirm(response);
    })
    .catch(err => {
      debugger
      console.log(err);
    });
  }, []);

  const handleChange = (value) => {
    debugger
  }

  const onInputChange= (evt) => {
    setQuantity(evt.target.value);
  }


  if (!token) {
    window.location.href = '/login';
  }


  return (
    <div>
      <UnitPrice className="d-flex flex-row justify-content-center">
        <CustomSelectOption className="order_column head">
          <select class="selectpicker">
          {cardBoards.map((card, index) => (
            <option value={card.price} key={index}>{`${card.card_type} (${card.price}AUD)`}</option>
          ))}
          </select>
        </CustomSelectOption>
        <CustomInput className="order_column head">
          <input 
            type="number" 
            value={quantity} 
            name="quantity"
            onChange={onInputChange}
          />
        </CustomInput>
      </UnitPrice>
    </div>
  );
};


export default NewOrder;
