import styled from 'styled-components';

export const AuthForm = styled.div`
  width: 40%;
  margin: 40px auto;
  background: #f3f3f3;
  padding: 3%;
  .input-label {
    display: flex !important;
  }
  input[type=checkbox]{
    margin-top: 6px;
    margin-right: 4px;
  }
`;

export const SubmitButton = styled.div`
  button {
    width: 150px;
    background: #6d6d71;
    color: white;
    &:hover {
      color: white !important;
    }
  }
`;
