/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from 'react';
import { getOr, get } from 'lodash/fp';
import { AuthForm, SubmitButton } from './AuthStyle';
import { VALID_EMAIL_REGEX, VALID_NAME_REGEX } from '../Constant';
import { MINIMUM_NAME_LENGTH, MAXIMUM_NAME_LENGTH, MAXIMUM_EMAIL_LENGTH } from '../ValidationLength';


const Signin = () => {
  const [formErrors, setFormErrors] = useState({});
  const [register, setRegister] = useState({
    email: '',
    password: '',
  });
  const {
    email,
    password,
  } = register;

  useEffect(() => {
    const errors = {
      email: '',
    };

    if (!email) {
      errors.email = '';
    } else if (!VALID_EMAIL_REGEX.test(email)) {
      errors.email = '*Please enter valid email';
    } else if (!(email.length <= MAXIMUM_EMAIL_LENGTH)) {
      errors.email = `Email should not exceed ${MAXIMUM_EMAIL_LENGTH} characters`;
    }
    console.log(register)
    console.log(errors)
    setFormErrors(errors);
  }, [email]);

  const onInputChange = (evt) => {
    setRegister({
      ...register,
      [evt.target.name]: evt.target.value,
    });
  };

  const onFormSubmit = (evt) => {
    evt.preventDefault();
    console.log(register)
    fetch("http://localhost:3000/api/v1/auth/register", {
      "method": "POST",
      "headers": {
        "content-type": "application/json",
        "accept": "application/json"
      },
      "body": JSON.stringify({
        name: name,
        email: email,
        password: password,
      })
    })
    .then(response => response.json())
    .then(response => {
      debugger
      console.log(response)
    })
    .catch(err => {
      debugger
      console.log(err);
    });
  };


  return (
    <AuthForm>
      <form
        onSubmit={onFormSubmit}
        method="post"
      >
        <div className="form-group">
            <label className="input-label" for="email">Email address:</label>
            <input 
              type="email" 
              name="email"
              className="form-control" 
              id="email" 
              required
              value={email}
              onChange={onInputChange}
              error={get('email', formErrors)}
            />
          </div>
          <div className="form-group">
            <label className="input-label" for="pwd">Password:</label>
            <input 
              type="password"
              name="password"
              className="form-control" 
              id="pwd"
              required
              value={password}
              onChange={onInputChange}
            />
          </div>
        <div className="checkbox">
          <label className="input-label"><input type="checkbox" /> Remember me</label>
        </div>
        <br></br>
        <SubmitButton className="form-group">
          <button type="submit" className="btn btn-default">Submit</button>
        </SubmitButton>
      </form>
    </AuthForm>
  );
};


export default Signin;
