import styled from 'styled-components';

export const OrderCell = styled.div`
  width: 97%;
  margin: 10px auto;
  background: #f3f3f3;
  padding: 10px;
  .order_column {
    width: 14.3% !important;
    text-align: left;
    height: 24px;
  }
  .head {
    font-weight: bold;
  }
`;
export const UnitPrice = styled.div`
  background: #f3f3f3;
`;
export const CustomSelectOption = styled.div`
  select {
    width: 300px;
    height: 40px;
    margin: 0px 20px;
    padding: 0px 0px;
    border: 1px solid #cacaca;
    border-radius: 4px;
  }
`;
export const CustomInput = styled.div`
  input {
    width: 200px;
    height: 40px;
    border: 1px solid #cacaca;
    border-radius: 4px;
  }
`;
